import { useState } from 'react'
import { v4 as uuidv4 } from 'uuid'
import TodoForm from './components/Todos/TodoForm';
import TodoList from './components/Todos/TodoList';
import TodosActions from './components/Todos/TodosActions';
import './App.css';

function App() {
  const [ todos, setTodos ] = useState([])

  const addTodoHandler = (text) => {
    const newTodo = {
      text: text,
      isCompletede: false,
      id: uuidv4(),
    }

    setTodos([...todos, newTodo])
  }

  const deleteTodoHandler = (id) => {
    setTodos(todos.filter((todo) => todo.id !== id))
  }

  const toggleTodoHendler = (id) => {
    setTodos(
      todos.map((todo) =>
      todo.id === id
      ? { ...todo,isCompleted: !todo.isCompleted }
      : { ...todo }
      )
    )
  }
  const resetTodosHandler = () => {
    setTodos([])
  }

  const deleteCompletedTodosHandler = () => {
    setTodos(todos.filter((todo) => !todo.isCompleted))
  }
  const comletedTodosCount = todos.filter((todo) => todo.isCompleted).length
  console.log(comletedTodosCount)
  return (
    <div className="App">
      <h1>TodoApp</h1>
      <TodoForm addTodo={addTodoHandler} />
      {!!todos.length &&(
      <TodosActions
      resetTodos={resetTodosHandler}
      deleteCompletedTodos={deleteCompletedTodosHandler}
      comletedTodosExist={!!comletedTodosCount}/>
      )}
      <TodoList 
      todos={todos} 
      deleteTodo={deleteTodoHandler}
      toggleTodo={toggleTodoHendler}
      />

      {comletedTodosCount > 0 && (
        <h2>{`You have completed ${comletedTodosCount} 
        ${comletedTodosCount > 1 ? 'todos' : 'todo'}`}</h2>
      )}
    </div>
  );
}

export default App;
